CREATE TABLE IF NOT EXISTS metadata (keyword VARCHAR(64) NOT NULL, value VARCHAR(64), UNIQUE(keyword));
CREATE TABLE locations (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(200) NULL, place VARCHAR(128) NOT NULL, postal_code VARCHAR(16) NULL, country VARCHAR(64) NULL, country_isocode VARCHAR(2) NOT NULL, geocoding_datetime_utc DATETIME NOT NULL, geocoding_provider VARCHAR(64) NOT NULL, geocoded_location_id VARCHAR(64) NOT NULL, geocoded_name VARCHAR(255) NOT NULL, geocoded_locality VARCHAR(64) NOT NULL, geocoded_postal_code VARCHAR(32) NULL, geocoded_latitude FLOAT NOT NULL, geocoded_longitude FLOAT NOT NULL, geocoded_country_isocode FLOAT NOT NULL, geocoded_timezone VARCHAR(32) NULL, UNIQUE(name, place, postal_code, country_isocode));
INSERT INTO metadata (keyword, value) VALUES ('locations_version', '1.0.0.0');
